background = Image.Create(AssetLocation.Disk, "/backgrounds/rico_sunset.jpg")
logoimg = ""..
	""..
	""..
	""
logo = Image.Create(AssetLocation.Base64, logoimg)
logo:SetSize(logo:GetSize() * 0.55)
mplogo = Image.Create(AssetLocation.Disk, "logo.png")
cloud = Image.Create(AssetLocation.Game, "cirrus_clouds_dif.dds")
plane = Image.Create(AssetLocation.Game, "bm_speedplane.dds")
heli1 = Image.Create(AssetLocation.Game, "bm_heliupgrade0.dds")
heli2 = Image.Create(AssetLocation.Game, "bm_attheliupgrade0.dds")

Events:Subscribe("ModuleLoad", function()

	Network:Send("ClientLoaded")

end)

Network:Subscribe("onPlayerJoin", function(args)
	draw_timer = Timer()
	active = true
	Chat:SetEnabled(false)
	if args.playtime ~= nil then

		playtime = args.playtime
		kills = args.kills
		deaths = args.deaths

	else

		playtime = nil

	end

end)

Events:Subscribe("GameLoad", function()
	loaded = true
end)

Events:Subscribe("PostRender", function()

	if draw_timer == nil then return end

	if not active then return end

	if draw_timer:GetSeconds() > 10 or loaded ~= nil then

		active = false
		Chat:SetEnabled(true)

	return end

		mplogo:SetPosition(Vector2(Render.Size.x * 0.51, Render.Size.y * 0.525) - mplogo:GetSize() * 3/4)
		logo:SetPosition(Vector2(Render.Size.x * 0.45, mplogo:GetPosition().y + mplogo:GetSize().y - 5))
		plane:SetPosition(Vector2(Render.Size.x * 0.2, Render.Size.y * 0.7) - plane:GetSize()/2)
		heli1:SetPosition(Vector2(Render.Size.x * 0.65, Render.Size.y * 0.25) - heli1:GetSize()/2)
		heli2:SetPosition(Vector2(Render.Size.x * 0.75, Render.Size.y * 0.75) - heli2:GetSize()/2)

		background:Draw()
		cloud:Draw(Vector2(0, 0), Render.Size, Vector2(0.5, 0), Vector2(1, 0.5))
		mplogo:Draw()
		logo:Draw()
		plane:Draw()
		heli1:Draw()
		heli2:Draw()

	local text = "Welcome to our server!"
    local fontsize = Render.Height / 1000 * 40
    local textsize = Render:GetTextSize(text, fontsize)
    Render:DrawText(Vector2(Render.Size.x * 0.2, Render.Size.y * 0.2) - textsize/2, text, Color.White, fontsize)

    local text = LocalPlayer:GetName()
    local fontsize = Render.Height / 1000 * 20
    local textsize = Render:GetTextSize(text, fontsize)
    Render:DrawText(Vector2(Render.Size.x * 0.2, Render.Size.y * 0.25) - textsize/2, text, Color.White, fontsize)

	if playtime ~= nil and kills ~= nil and deaths ~= nil then

		local text = string.format("Statistics for %s", LocalPlayer:GetName())
		local fontsize = Render.Height / 1000 * 20
    	local textsize = Render:GetTextSize(text, fontsize)
		Render:DrawText(Vector2(Render.Size.x * 0.9, Render.Size.y * 0.475) - Vector2(textsize.x/2, textsize.y/2), text, Color.White, fontsize)

		local hours = math.modf(playtime / 60)
		local minutes = math.modf(playtime - hours * 60)
		local text = string.format("Kills: %s Deaths: %s", kills, deaths)
		local fontsize = Render.Height / 1000 * 15
		local textsize = Render:GetTextSize(text, fontsize)
		Render:DrawText(Vector2(Render.Size.x * 0.9, Render.Size.y * 0.475) - Vector2(textsize.x/2, -textsize.y * 1.5), text, Color.White, fontsize)

		local text = string.format("Playtime: %s hours, %s minutes", hours, minutes)
		local textsize = Render:GetTextSize(text, fontsize)
		Render:DrawText(Vector2(Render.Size.x * 0.9, Render.Size.y * 0.475) - Vector2(textsize.x/2, -textsize.y * 3), text, Color.White, fontsize)

	else

		local text = "You're new to our server!"
		local fontsize = Render.Height / 1000 * 20
    	local textsize = Render:GetTextSize(text, fontsize)
		Render:DrawText(Vector2(Render.Size.x * 0.9, Render.Size.y * 0.475) - Vector2(textsize.x/2, textsize.y/2), text, Color.White, fontsize)

		local text = "Our website: www.JC2ATC.ovh"
		local textsize = Render:GetTextSize(text, fontsize)
		Render:DrawText(Vector2(Render.Size.x * 0.9, Render.Size.y * 0.475) - Vector2(textsize.x/2, -textsize.y * 1.5), text, Color.White, fontsize)

	end

end)